<?php

namespace App\Repository;

use App\Entity\ApproVersements;
use Doctrine\Bundle\DoctrineBundle\Repository\ServiceEntityRepository;
use Doctrine\ORM\Tools\Pagination\Paginator;
use Symfony\Bridge\Doctrine\RegistryInterface;

/**
 * ClientsRepository
 *
 * This class was generated by the PhpStorm "Php Annotations" Plugin. Add your own custom
 * repository methods below.
 */

class ApproVersementsRepository extends ServiceEntityRepository
{
    public function __construct(RegistryInterface $registry)
    {
        parent::__construct($registry, ApproVersements::class);
    }


    public function liste($offset,$limit = 20)
    {
        $qb = $this->createQueryBuilder('a')
           // ->innerJoin('c.compenseLignes','cl', 'WITH')
            ->orderBy('a.dateOperation', 'DESC')
            ->setFirstResult($offset)
            ->setMaxResults($limit)
            //->getQuery()
            //->getResult()
            ;
        $pag = new Paginator($qb);

        return $pag;
    }


}
